mapper.select:
select st.state,st.result,st.create_time,st.manager_name as `managerName`, st.customer_name as `customerName`, st.create_time as `createTime`, st.id,st.order_id as `orderId`,st.describes, sor.plat_order_name as `orderNumber`,sor.store_name as `storeName` from support_tickets st left join store_order_relate sor on st.order_id = sor.order_id
order by st.state asc,st.create_time desc
limit #{fromNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

where.if:
1 = 1

    and  st.customer_id = #{t.customerId}


    and st.state = #{t.state}


    and  sor.plat_order_name = #{t.orderNumber}


    and sor.store_name = #{t.storeName}


    and st.manager_customer_id = #{t.managerCustomerId}


    and sor.order_id = #{orderId}

mapper.update:
update support_tickets
id = #{id}, order_id = #{orderId}, customer_id = #{customerId}, customer_name = #{customerName}, state = #{state}, result = #{result}, describes = #{describes}, create_time = #{createTime}, finish_time = #{finishTime}, times_count = #{timesCount}, last_source = #{lastSource},
where id = #{id}

