mapper.update:
update product_variant
id = #{id}, original_variant_id = #{originalVariantId}, product_id = #{productId}, variant_sku = #{variantSku}, variant_image = #{variantImage}, variant_price = #{variantPrice}, usd_price = #{usdPrice}, inventory = #{inventory}, weight = #{weight}, state = #{state}, volume_weight = #{volumeWeight}, variant_type = #{variantType}, cn_name = #{cnName}, en_name = #{enName}, length = #{length}, width = #{width}, height = #{height},
where id = #{id}

update product_variant
set weight=#{weight}
where id in

    #{item}

update product_variant
set variant_price=#{price},usd_price = #{usdPrice}
where id in

    #{item}

update product_variant
set variant_image=#{variantImage}
where id in

    #{item}

update.foreach:
update product_variant
set cn_name=#{item.cnName},
en_name=#{item.enName}
where id=#{item.id}

mapper.select:
select

from product_variant


    order by ${orderBy}


    limit #{fromNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
from product_variant where id in
#{item}

select '0' as store_variant_id,'0' as store_product_id, pv.variant_price as quote_price, pv.id as variantId,pv.en_name as variantName,pv.variant_sku, pv.variant_image,pv.variant_price as `cny_price`,pv.usd_price, pv.weight,pv.volume_weight as `volume`,p.product_title as `productTitle`, p.shipping_id as productShippingId,p.id as productId,'1' as quote_scale, pv.width,pv.height,pv.length,'1' as `quoteScale`, '1' as quoteType,'1' as quote_state from product_variant pv left join product p on p.id = pv.product_id where pv.variant_sku in
#{sku}
and pv.state = '1'

update.foreach:
update product_variant
set saihe_sku = #{variant.saiheSku}
where variant_sku = #{variant.variantSku}
limit 1
