mapper.select:
select

from variant_warehouse_stock
where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}

mapper.delete:
delete
from variant_warehouse_stock
where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}

mapper.update:
update variant_warehouse_stock
variant_id = #{variantId}, warehouse_code = #{warehouseCode}, stock_scale = #{stockScale}, available_stock = #{availableStock}, lock_stock = #{lockStock}, purchase_stock = #{purchaseStock}, remark = #{remark}, shelf_num = #{shelfNum},
where variant_id = #{variantId}

mapper.insert:
insert into variant_warehouse_stock


        variant_id


        warehouse_code


        stock_scale


        available_stock


        lock_stock


        purchase_stock


        remark


        shelf_num




        #{variantId}


        #{warehouseCode}


        #{stockScale}


        #{availableStock}


        #{lockStock}


        #{purchaseStock}


        #{remark}


        #{shelfNum}

mapper.select:
select

from variant_warehouse_stock
where warehouse_code = #{warehouseCode}
and variant_id in

    #{variantId}

and stock_state = '1'

mapper.update:
update variant_warehouse_stock
set available_stock = available_stock - #{changeQuantity},
    lock_stock = lock_stock + #{changeQuantity}
where variant_id = #{variantId}
  and warehouse_code = #{warehouseCode}
  and available_stock >= #{changeQuantity}

update variant_warehouse_stock
set purchase_stock = purchase_stock + #{changeQuantity}
where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}

update variant_warehouse_stock
set available_stock = available_stock - #{quantity}
where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}
and available_stock > #{quantity}

update variant_warehouse_stock
set available_stock = available_stock + #{quantity},
stock_state = '1'

    ,purchase_stock = purchase_stock - #{quantity}

where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}

    and purchase_stock >= #{quantity}

update variant_warehouse_stock
set safe_stock = #{safeStock}
where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}

update variant_warehouse_stock
set lock_stock = lock_stock - #{changeQuantity}
where variant_id = #{variantId}
  and warehouse_code = #{warehouseCode}
  and lock_stock >= #{changeQuantity}

mapper.select:
select vws.`variant_id`,vws.`safe_stock`,vws.`warehouse_code`,vws.`stock_scale`,vws.`available_stock`,
vws.`lock_stock`,vws.`purchase_stock`,vws.`remark`,vws.`shelf_num`,vws.`update_time`,
pv.cn_name,pv.purchase_sku,pv.variant_sku,pv.variant_image,pv.barcode
from variant_warehouse_stock vws
inner join product_variant pv
on vws.variant_id = pv.id

    stock_state = '1'

        and pv.variant_sku = #{t.variantSku}


        and pv.barcode = #{t.barcode}


        and pv.purchase_sku = #{t.purchaseSku}


        and pv.id = #{t.id}


order by available_stock desc

    limit #{fromNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(pv.id)
    from variant_warehouse_stock vws
inner join product_variant pv
on vws.variant_id = pv.id

    stock_state = '1'

        and pv.variant_sku = #{t.variantSku}


        and pv.barcode = #{t.barcode}


        and pv.purchase_sku = #{t.purchaseSku}


        and pv.id = #{id}

mapper.update:
update variant_warehouse_stock
    set stock_state = '0',
        available_stock = '0',
        lock_stock = '0'
where variant_id = #{variantId}
and warehouse_code = #{warehouseCode}
and stock_state = '1'
and purchase_stock = '0'
and lock_stock = '0';

update variant_warehouse_stock
set lock_stock = lock_stock - #{changeQuantity},
    available_stock = available_stock + #{changeQuantity}
where variant_id = #{variantId}
  and warehouse_code = #{warehouseCode}
  and lock_stock >= #{changeQuantity}
