mapper.update:
update customer_product_quote
store_variant_id = #{storeVariantId}, customer_id = #{customerId}, store_product_id = #{storeProductId}, product_id = #{productId}, variant_id = #{variantId}, store_variant_name = #{storeVariantName}, store_product_title = #{storeProductTitle}, store_variant_sku = #{storeVariantSku}, store_variant_image = #{storeVariantImage}, product_title = #{productTitle}, variant_name = #{variantName}, variant_sku = #{variantSku}, variant_image = #{variantImage}, quote_price = #{quotePrice}, quote_apply_id = #{quoteApplyId}, quote_state = #{quoteState}, quote_scale = #{quoteScale}, store_parent_variant_id = #{storeParentVariantId},
where store_variant_id = #{storeVariantId}

