mapper.select:
select

from customer_product_quote


    order by ${orderBy}


    limit #{fromNum,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select

from customer_product_quote
where customer_id = #{customerId}
and store_variant_id in

    #{storeVariantId}

select cpq.store_variant_id,cpq.store_product_id,cpq.quote_apply_id, cpq.quote_price,cpq.customer_id,cpq.store_variant_image, pv.id as variantId,pv.en_name as variantName,pv.variant_sku, pv.variant_image,pv.variant_price as `cny_price`,pv.usd_price, pv.weight,pv.volume_weight as `volume`,p.product_title as `productTitle`, p.shipping_id as productShippingId,p.id as productId,cpq.quote_scale, pv.width,pv.height,pv.length,ifnull(cpq.quote_scale,'1') as `quoteScale`, '1' as quoteType,cpq.quote_state,store_parent_variant_id, store_variant_name,store_variant_sku from customer_product_quote cpq left join product_variant pv on cpq.variant_id = pv.id left join product p on p.id = pv.product_id
1 = 1
and cpq.customer_id = #{customerId}
and cpq.quote_apply_id = #{quoteApplyId}
and cpq.variant_id = #{variantId}
and cpq.store_variant_id = #{storeVariantId}
and cpq.store_product_id = #{storeProductId}
and cpq.store_variant_id in
#{variantId}
and cpq.store_product_id in
#{productId}
